sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	var user=g_form.getValue('u_requested_for');

   //Type appropriate comment here, and begin script below
	var gr=new GlideAjax('getUserDetails');
	gr.addParam('sysparm_name','getUserValues');
	gr.addParam('sys_user_id',user);
	gr.getXML(populateDetails);

	function populateDetails(response){
		alert('Inside Onchange');

		  var answer=response.responseXML.documentElement.getAttribute("answer");


    alert(answer); //alert to see the string value


    var answer2 = answer.evalJSON();

		alert('UserName'+answer2.userName);
		alert('dept'+answer2.department);
		alert('lang'+answer2.lang);
		alert('active'+answer2.active);

		g_form.setValue('u_name',answer2.userName);
		g_form.setValue('u_department',answer2.department);
		g_form.setValue('u_lang',answer2.lang);
		g_form.setValue('u_active',answer2.active);

	}

}
