sys_script_include.script:
var getUserDetails = Class.create();
getUserDetails.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getUserValues:function(){

		var userId=this.getParameter('sys_user_id');



		var userReturn ={};

		var userGR= new GlideRecord('sys_user');
		userGR.addQuery('sys_id',userId);
		userGR.setLimit(1);
		userGR.query();

		if(userGR.next()){

			userReturn.userName=userGR.user_name.toString();
			userReturn.department=userGR.department.toString();
			userReturn.active=userGR.active.toString();
			userReturn.lang=userGR.preferred_language.toString();
		}



		var sample=new global.JSON().encode(userReturn);
		gs.info('Abi'+sample);

		return new global.JSON().encode(userReturn);
	},

    type: 'getUserDetails'
});
